import service from "@/utils/request";

//登录 2022/3/30 9:21
export function Login(data){
    return service({
        url:'/api/user/login',
        method:'post',
        data:data
    })
}

//注册2022/3/30 10:21
export function Register(data){
    return service({
        url:'/api/user/register',
        method:'post',
        data:data
    })
}

//查询用户信息2022/3/30 11:13
export function UserInfo(data){
    return service({
        url:'/api/user/id',
        method:'get',
        data:data
    })
}
