  state: {
    //检测是否登录
    islogin:true,
    //用户设置
    now_user:{
      user_name:"ice",

    },
    navnum:0,
    //控制Header的点击颜色
    color1:{color:"#5b5d63"},color2:{color:"#5b5d63"},color3:{color:"#5b5d63"},color4:{color:"#5b5d63"},
    //选中的电影，重要设置，用来检测最后一个点的电影
    SelectFilm:{film_name:"狮子王" ,film_img:"",film_type:"",film_region:"",film_date:""},
  },
