import service from "@/utils/request";

//模糊查询实际2022/3/31 8:37
export function SearchFilm(data){
    return service({
        url:'/api/film/name',
        method:'get',
        data:data
    })
}

//查询电影列表 2022/3/31 9:51

export function QueryFilmList(data){
    return service({
        url:'/api/film',
        method:'get',
        data:data
    })
}

//查询轮播图海报 2022/3/31 9:51

export function ListAllPost(state){
    return service({
        url:'/api/post',
        method:'get',
        data:state
    })
}
